import { useState } from "react";
import { useEffect } from "react";

import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';


import './style.css'

export default function EventContent(){

    const [textValue,setTextValue]=useState('')

    const handleOnChange=(e,editor)=>{
        console.log(editor.getData())
    }

    const [tHead,setTHead]=useState()
    // const []
    
    const [data,setData]=useState(
        [
            {
                stt:0,
                col1:'dat',
                col2:10000,
                col3:'Click de xem chi tiet',
            },
            {
                stt:1,
                col1:'dat',
                col2:10000,
                col3:'Click de xem chi tiet',
            },
            {
                stt:2,
                col1:'dat',
                col2:10000,
                col3:'Click de xem chi tiet',
            },
            {
                stt:3,
                col1:'dat',
                col2:10000,
                col3:'Click de xem chi tiet',
            },
        ]
    )
   

    return(
        <div className='blogList' style={{width:'80vw',margin:'auto'}}>
            <div className="bloglistTitle">
                <div style={{textAlign:'center',fontSize:'24px',fontFamily:'Source Sans Pro'}}>
                    Danh sách bài viết
                </div>
            </div>
            <div className='blogListTools' style={{display:'flex',justifyContent:'space-between'}}>
                <div className='blogListToolsSearch' style={{display:'flex',justifyContent:'space-between'}}>
                    <input type="text" placeholder="Mã bài viết" style={{height:'20px'}} />
                    <div>
                        
                    </div>
                    <label htmlFor="">Người đăng</label>
                    <select name="" id="">
                        <option value="0">Tất cả</option>
                    </select>
                </div>
                <div className="addBlog" style={{height:'30px'}} >
                    <button style={{height:'100%',width:'100%'}}>Thêm bài viết</button>
                </div>
            </div>
            
            <table className='blogList-table'>
                <tr>
                    <th>STT</th>
                    <th>Ten</th>
                    <th>Luot tuong tac</th>
                    <th>Chi tiet</th>
                </tr>
                
                    {
                        data.map((element)=>{
                            return(
                                <tr>
                                    {
                                        Object.keys(element).map(function(key,index){
                                            if(key=='col3') return <td><button>{element[key]}</button></td>
                                            return <td>{element[key]}</td>
                                        })
                                    }
                                </tr>
                            )
                        })
                    }
                
            </table>


            <CKEditor
                editor={ClassicEditor}
                onChange={handleOnChange}
            >

            </CKEditor>
        </div>
    )
}
