import { useState } from "react";
import { useEffect } from "react";

import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';


import './style.css'

export default function EventContent(){

    const [textValue,setTextValue]=useState('')

    const handleOnChange=(e,editor)=>{
        console.log(editor.getData())
    }

    const [tHead,setTHead]=useState()
    // const []
    
    const [data,setData]=useState(
        [
            {
                col1:'dat',
                col2:10000,
                col:'Click de xem chi tiet',
            },
            {
                col1:'dat',
                col2:10000,
                col3:'Click de xem chi tiet',
            },
            {
                col1:'dat',
                col2:10000,
                col3:'Click de xem chi tiet',
            },
            {
                col1:'dat',
                col2:10000,
                col3:'Click de xem chi tiet',
            },
        ]
    )
    const [sttColumn,setSttColumn]=useState([])

    
    useEffect(() => {
        var stt=[]
        for(var i=1;i<=data.length;i++){
            stt.push(i);
        }
        setSttColumn(stt);
    }, [])

    return(
        <div className='blogList'>
            <table>
               
                    {
                        sttColumn.map((element)=>{
                            return <th>{element}</th>
                        })
                    }
                
            </table>
            <table className='blogList-table'>
                <tr>
                    <th>Ten</th>
                    <th>Luot tuong tac</th>
                    <th>Chi tiet</th>
                </tr>
                
                    {
                        data.map((element)=>{
                            return(
                                <tr>
                                    {
                                        Object.keys(element).map(function(key,index){
                                            if(key=='col3') return <td><button>{element[key]}</button></td>
                                            return <td>{element[key]}</td>
                                        })
                                    }
                                </tr>
                            )
                        })
                    }
                
            </table>


            <CKEditor
                editor={ClassicEditor}
                onChange={handleOnChange}
            >

            </CKEditor>
        </div>
    )
}
