import { useState } from "react"

import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

import ReactHtmlParser from 'react-html-parser'; 



export default function AddBlog(){
    const [textValue,setTextValue]=useState()

    const handleOnChange=(e,editor)=>{
        const text=editor.getData();
        if(text=='') return ;

        setTextValue(ReactHtmlParser(text));
        //console.log(editor.getData())
    }
    const [language,setLanguage]=useState({
        blogPageTitle:'Thêm bài viết',
        
    })
    
    const [style,setStyle]=useState(
        {
            textCenter:{
                textAlign:'center',fontSize:'24px',fontFamily:'Source Sans Pro',
            },
            addBlog:{
                width: '80vw',
                backgroundColor: 'rgb(226, 226, 226)',
                margin:'auto',
            },
            toolsAdd:{
                
            },
            defaultImg:{
                objectFit:'cover',
                height:'100%',
            },
            

            iconTools:{
                width:'30px',
                height:'30px',
                margin:'0px 10px 0px 0px',

                "&:hover":{
                    backgroundColor:'red',
                },
            },
        }
    );
    const imgLink={
        textIcon:'https://cdn-icons.flaticon.com/png/128/3815/premium/3815460.png?token=exp=1637497950~hmac=b836f417222df5d095bff7a3c899bad1',
        imgIcon:'https://cdn-icons-png.flaticon.com/128/834/834096.png',
    };
  
    return(
        <div className="addBlog" style={style.addBlog}>
            <div className="addBlogTitle" style={style.textCenter}>
                <div>{language.blogPageTitle}</div>
            </div>
            {
                textValue
            }
            <div className="editor" style={{position:'relative',}}>
                <CKEditor
                    editor={ClassicEditor}
                    onChange={handleOnChange}
                >
                </CKEditor>
                <div style={{position:'absolute',top:'0',right:'0',display:'flex'}}>  
                    <div className="customAlign" style={{width:'30px',height:'35px',marginLeft:'10px'}}>
                        <img src="https://cdn-icons-png.flaticon.com/128/847/847621.png" alt="" style={{width:'100%',objectFit:'cover',padding:'5px 0px 0px 0px'}}/>
                    </div>
                    <div className="customAlign" style={{width:'30px',height:'35px',marginLeft:'10px'}}>
                        <img src="https://cdn-icons-png.flaticon.com/128/847/847497.png" alt="" style={{width:'100%',objectFit:'cover',padding:'5px 0px 0px 0px'}}/>
                    </div>
                    <div className="customAlign" style={{width:'30px',height:'35px',marginLeft:'10px'}}>
                        <img src="https://cdn-icons-png.flaticon.com/128/847/847499.png" alt="" style={{width:'100%',objectFit:'cover',padding:'5px 0px 0px 0px'}}/>
                    </div>
                </div>
            </div>    
            <div style={style.toolsAdd}>
                <div style={{display:'flex',justifyContent:'left',width:'auto',}}>
                    <div style={style.iconTools}>
                        <img src={imgLink.textIcon} style={style.defaultImg} alt="" />
                    </div>
                    <div style={style.iconTools}>
                        <img src={imgLink.imgIcon} style={style.defaultImg} alt="" />
                    </div>
                </div>
            </div>    
        </div>
        
    )
}
